  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter selected={Year} onChangeFilter={filterChangeHandler} />
        {props.expenses.map((expense, index) => {
          if (expense.date.getFullYear() == Year) { 
            return (
            <ExpenseItem
              key={index}
              title={expense.title}
              amount={expense.amount}
              date={expense.date}
            />
        )}
            })}
      </Card>
    </div>
  );
}

export default Expenses;
